package basicjava;

public class Char {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		char  aChar, bChar;
		aChar = 88; 		// code for X
		bChar = 'Y';
		System.out.print("aChar and bChar : ");
		System.out.println(aChar + " " + aChar);

		aChar++; 			// increment aChar
		System.out.println("aChar :  " + aChar);
	}

}
